        ];
    }

    public static function getRestaurantItems(){
        return [
            [
                'icon' => 'inventory',
                'name' => 'Category',
                'path' => '/inventory',
            ],
            [
                // Changed from 'stock' to 'folders'
                'icon' => 'folders',
                'name' => 'Menu',
                'path' => '/stock',
            ],
            [
                // Changed from 'table' to 'table_new'
                'icon' => 'table_new',
                'name' => 'Tables',
                'path' => '/tables',
            ],
        ];
    }

    public static function getMenuGroups()
    {
        return [
            [
                'title' => 'Menu',
                'items' => self::getMainNavItems()
            ],
            [
                'title' => 'Hotel',
                'items' => self::getHotelItems()
            ],
            [
                'title' => 'Resturant',
                'items' => self::getRestaurantItems()
            ]
        ];
    }

    public static function isActive($path)
    {
        return request()->is(ltrim($path, '/'));
    }

